import React, { Component } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";


export default class CreatePatient extends Component {
    constructor(props){
        super(props);

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangeDOB = this.onChangeDOB.bind(this)
        this.onChangePhonenumber = this.onChangePhonenumber.bind(this);
        this.onChangeAddress = this.onChangeAddress.bind(this);
        this.onChangeFamilySize = this.onChangeAddress.bind(this);
        this.onChangePrescription = this.onChangePrescription.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            email: '',
            dob: new Date(),
            phonenumber: 0,
            address: '',
            familysize: '',
            prescription: '',
            prescriptions: []
        }
    }

    onChangeName(e){
        this.setState({
            name: e.target.value
        })
    }

    onChangeEmail(e){
        this.setState({
            email: e.target.value
        })
    }

    onChangeDOB(date){
        this.setState({
            date: date
        })
    }

    onChangePhonenumber(e){
        this.setState({
            phonenumber: e.target.value
        })
    }

    onChangeAddress(e){
        this.setState({
            address: e.target.value
        })
    }

    onChangeFamilySize(e){
        this.setState({
            familysize: e.target.value
        })
    }

    onChangePrescription(e){
        this.setState({
            prescription: e.target.value
        })
    }

    onSubmit(e){
        e.preventDefault();

        const patient = {
            name: this.state.name,
            email: this.state.email,
            dob: this.state.dob,
            phonenumber: this.state.phonenumber,
            address: this.state.address,
            familysize: this.state.familysize,
            prescription: this.state.prescription
        }

        console.log(patient)
    }

    render(){
        return(
            <>
                <section class="joinnetwork sec-apply">
                    <div class="container">
                        <div class="row text-center">
                            <div class="col-md-12">
                                <div class="border-line text-center"></div>
                                <h1>SIGN UP</h1>
                            </div>
                        </div>
                    </div>
                </section>

                <div class="container">
                    <div class="flip-sp">
                        <center>
                            <div class="icon">
                                <img src={require('./img/logo-small.png')} />
                            </div>
                        </center>
                    </div>
                </div>

                <section class="contact-section area-padding">
                    <div class="container">
                        <div class="row justify-content-center">
                            <div class="col-lg-8">
                                <form class="form-contact contact_form" onSubmit={this.onSubmit} >
                                    <div class="row">
                                        <div class="col-sm-12">
                                            <div class="form-group">
                                                <input class="form-control" name="name" id="name" type="text" placeholder="NAME" value={this.state.name} onChange={this.onChangeName}/>
                                            </div>
                                        </div>
                                        <div class="col-sm-12">
                                            <div class="form-group">
                                                <input class="form-control" name="email" id="email" type="email" placeholder="EMAIL" value={this.state.email} onChange={this.onChangeEmail}/>
                                            </div>
                                        </div>
                                        <div class="col-sm-12">
                                            <label>DATE OF BIRTH : </label>
                                                <div>
                                                    <DatePicker
                                                    selected={this.state.date}
                                                    onChange={this.onChangeDate}
                                                    />
                                                </div>
                                        </div>
                                        <div class="col-sm-12">
                                            <div class="form-group">
                                                <input class="form-control" name="phone" id="phone" type="text" placeholder="PHONE NUMBER"></input>
                                            </div>
                                        </div>
                                        <div class="col-sm-12">
                                            <div class="form-group">
                                                <input class="form-control" name="famsize" id="famsize" type="text" placeholder="FAMILY SIZE"></input>
                                            </div>
                                        </div>
                                        <div class="col-sm-4">
                                            <div class="form-group">
                                                <select id="state" class="form-control" name="prescription" form="prescription">
                                                    <option value="STATE">PRESCRIPTION</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div class="form-group mt-3 text-center">
                                            <button type="submit" class="button button-contactForm">SUBMIT</button>
                                        </div>
                                    </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </section>
                </>         
        )
    }
}
