import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Patient = props => (
    <tr>
      <td>{props.patient.name}</td>
      <td>{props.patient.email}</td>
      <td>{props.patient.dob.substring(0,10)}</td>
      <td>{props.patient.phonenumber}</td>
      <td>{props.patient.address}</td>
      <td>{props.patient.familysize}</td>
      <td>{props.patient.prescription}</td>
    </tr>
)

export default class Dashboard extends Component {

    constructor(props) {
        super(props);
        this.state = {patients: []};
    }

    componentDidMount() {
        axios.get('http://localhost:5000/patients/')
          .then(response => {
            this.setState({ patients: response.data })
          })
          .catch((error) => {
            console.log(error);
          })
    }

    patientList() {
        return this.state.patients.map(currentpatient => {
          return <Patient patient={currentpatient} key={currentpatient._id}/>;
        })
      }

    render(){
        return(
            <>
            <section class="joinnetwork sec-apply">
                <div class="container">
                    <div class="row text-center">
                        <div class="col-md-12">
                            <div class="border-line text-center"></div>
                            <h1>DASHBOARD</h1>
                        </div>
                    </div>
                </div>
            </section>

            <div class="container">
                <div class="flip-sp">
                    <center>
                        <div class="icon">
                            <img src={require('./img/logo-small.png')} />
                        </div>
                    </center>
                </div>
            </div>

            <div>    
                <table>
                    <thead>
                        <tr>
                            <th>Patient Name</th>
                            <th>Email<i class="fa fa-sort"></i></th>
                            <th>Date of Birth<i class="fa fa-sort"></i></th>
                            <th>Phonenumber<i class="fa fa-sort"></i></th>
                            <th>Address<i class="fa fa-sort"></i></th>
                            <th>Family Size<i class="fa fa-sort"></i></th>
                            <th>Prescription<i class="fa fa-sort"></i></th>
                        </tr>

                    </thead>
                <tbody>
                    { this.patientList() }
                </tbody>
                </table>
            </div>
            </>         
        )
    }
}
